mapper.update:
update product
   set count = count + 1
 where product_no = #{productNo}

mapper.insert:
insert
  into product
  (
  	product_no
  , product_name
  , price
  , sale
  , explanation
  , location
  , production_date
  , stock
  )
  values
  (
  	seq_product_no.nextval
  , #{productName}
  , #{price}
  , #{sale}
  , #{explanation}
  , #{location}
  , #{productionDate}
  , #{stock}
  )

insert
  into Attachment
  (
    file_no
  , ref_pno
  , origin_name
  , change_name
  , file_exp
  , file_level
  )
  values
  (
    seq_attachment_no.nextval
  , seq_product_no.currval
  , #{originName}
  , #{changeName}
  , #{fileExp}
  , #{fileLevel}
  )
