mapper.select:
select
       count(*)
  from qa
 where q_status = 'Y'
   and user_id = #{userId}

   and qa_category = #{categoryNo}

select qa_no , category_name , SUBSTR(user_id, 1,2) || RPAD('*', LENGTH(user_id) - 2, '*') AS user_id , to_char(q_date, 'YYYY-MM-DD') as "q_date" , a_status from qa join qa_category on (qa_category = category_no) where q_status = 'Y' and user_id = #{userId}
and qa_category = #{categoryNo}
order by qa_no desc

mapper.insert:
insert
  into qa
     (
        qa_no
      , qa_category
      , user_id
      , q_title
      , q_content
      )
      values
      (
        seq_qa_no.nextval
      , #{qaCategory}
      , #{userId}
      , #{qaTitle}
      , #{qaContent}
      )
